// SPDX-License-Identifier: MIT
pragma solidity 0.8.15.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./interfaces/IBridge.sol";
import "./Token.sol";
import "./Authorizable.sol";
import "./libraries/Structs.sol";

/**
 * @title Bridge
 * 
 * @notice A bridge for DEX operations. Implements IBridge interface
 */
contract Bridge is IBridge, Authorizable, ReentrancyGuard {
    /// @notice Instance of Token smart contract
    IERC20 private token;

    /// @notice A global burn nonce. Its first value should be 1, not 0
    uint256 private burnNonce = 0;

    /// @notice Amount of user addresses, which at least once executed 'mint' function
    uint256 private userAddressesCounter = 0;

    /// @notice Contains all the user addresses, which at least once executed 'mint' function
    mapping(uint256 => address) private userAddresses;

    /// @notice Contains a burn nonce attached to the user address
    mapping(address => uint256) public userNonceMinted;

    constructor(IERC20 token_, IAuthorizers authorizers_)
        Authorizable(authorizers_){
            token = token_;
        }

    /**
     * @notice A modifier, which throws error, if given nonce already was used before
     * @param to_ The address to mint tokens to
     * @param nonce_ The nonce of mint transaction
     */
    modifier notMinted(address to_, uint256 nonce_) {
        require(
            userNonceMinted[to_] + 1 == nonce_,
            "Provided nonce must be 1 greater than the previous burn nonce."
        );
        _;
    }

    /**
     * @notice A function, which returns mint nonce for a specified ethereum address
     * @param to_ The address used for mint function calls 
     * @return uint256 The nonce used during burn function calls
     */
    function getUserNonceMinted(address to_) public view returns (uint256){
        return userNonceMinted[to_];
    }

    /**
     * @notice A function, which is used to reset all the mint nonces.
     */
    function resetUserNonceMinted() public onlyOwner {
        for (uint i = 0; i <= userAddressesCounter; i++) {
            delete userNonceMinted[userAddresses[i]];
        }
    }

    /**
     * @dev see {IBridge-burn}
     */
    function burn(uint256 amount_, bytes calldata clientId_) external override {
        _burn(msg.sender, amount_, clientId_);
    }

    /**
     * @notice An implementation of the burn function
     * @param from_ The address to burn tokens from
     * @param amount_ The amount of tokens to burn
     * @param clientId_ The 0chain client ID of the burner
     */
    function _burn(
        address from_,
        uint256 amount_,
        bytes memory clientId_
    ) private nonReentrant {
        require(
            token.transferFrom(from_, address(this), amount_),
            "Bridge: transfer into burn pool failed"
        );
        burnNonce = burnNonce + 1;

        emit Burned(from_, amount_, clientId_, burnNonce);
    }

    /**
     * @dev see {IBridge-mint}
     */
    function mint(
        address to_,
        uint256 amount_,
        bytes calldata txid_,
        uint256 nonce_,
        bytes[] calldata signatures_
    ) external override notMinted(to_, nonce_) {
        Structs.Args memory args = Structs.Args(
            to_,
            amount_,
            txid_,
            nonce_
        );

        bytes32 message = authorizers.messageHash(
            to_,
            amount_,
            txid_,
            nonce_
        );

        _mint(args, message, signatures_);
    }

    /**
     * @notice An implementation of the mint function
     * @param args_ The arguments for message
     * @param message_ The message generated and signed by authorizers
     * @param signatures_ The validated signatures concatenated
     */
    function _mint(
        Structs.Args memory args_,
        bytes32 message_,
        bytes[] calldata signatures_
    ) private nonReentrant isAuthorized(message_, signatures_) {
        require(
            token.transferFrom(address(this), args_.to, args_.amount),
            "Bridge: transfer out of pool failed"
        );

        // solhint-disable-next-line
        userNonceMinted[args_.to] = args_.nonce;
        // solhint-disable-next-line
        userAddresses[userAddressesCounter] = args_.to;
        userAddressesCounter++;

        emit Minted(
            args_.to,
            args_.amount,
            args_.txid,
            args_.nonce
        );
    }
}

